<?php

namespace App\Models;

use YAPF\Framework\DbObjects\GenClass\GenClass as GenClass;
use App\Models\Sets\NoticeSet as NoticeSet;
use App\Models\Sets\PackageSet as PackageSet;

// Do not edit this file, rerun gen.php to update!
class Noticenotecard extends genClass
{
    protected $use_table = "noticenotecard";
    // Data Design
    protected $fields = [
        "id",
        "name",
        "missing",
    ];
    protected $dataset = [
        "id" => ["type" => "int", "value" => null],
        "name" => ["type" => "str", "value" => null],
        "missing" => ["type" => "bool", "value" => 0],
    ];
    // Setters
    /**
    * setName
    * @return mixed[] [status =>  bool, message =>  string]
    */
    public function setName(?string $newvalue): array
    {
        return $this->updateField("name", $newvalue);
    }
    /**
    * setMissing
    * @return mixed[] [status =>  bool, message =>  string]
    */
    public function setMissing(?bool $newvalue): array
    {
        return $this->updateField("missing", $newvalue);
    }
    // Getters
    public function getName(): ?string
    {
        return $this->getField("name");
    }
    public function getMissing(): ?bool
    {
        return $this->getField("missing");
    }
    // Loaders
    public function loadByName(string $name): bool
    {
        return $this->loadByField(
            "name",
            $name
        );
    }
    public function loadByMissing(bool $missing): bool
    {
        return $this->loadByField(
            "missing",
            $missing
        );
    }
    public function relatedNotice(): NoticeSet
    {
        $ids = [$this->getId()];
        $collection = new NoticeSet();
        $collection->loadFromNoticeNotecardLinks($ids);
        return $collection;
    }
    public function relatedPackage(): PackageSet
    {
        $ids = [$this->getId()];
        $collection = new PackageSet();
        $collection->loadFromWelcomeNotecardLinks($ids);
        return $collection;
    }
}
