<?php

namespace App\Models;

use YAPF\Framework\DbObjects\GenClass\GenClass as GenClass;
use YAPF\Framework\Responses\DbObjects\UpdateReply as UpdateReply;
use YAPF\Framework\Responses\DbObjects\SingleLoadReply as SingleLoadReply;
use App\Models\Sets\AvatarSet as AvatarSet;
use App\Models\Sets\TransactionsSet as TransactionsSet;

// Do not edit this file, rerun gen.php to update!
class Reseller extends genClass
{
    protected $use_table = "reseller";
    // Data Design
    protected $fields = [
        "id",
        "avatarLink",
        "allowed",
        "rate",
    ];
    protected $dataset = [
        "id" => ["type" => "int", "value" => null],
        "avatarLink" => ["type" => "int", "value" => null],
        "allowed" => ["type" => "bool", "value" => 0],
        "rate" => ["type" => "int", "value" => 0],
    ];
    // Setters
    /**
    * setAvatarLink
    */
    public function setAvatarLink(?int $newValue): UpdateReply
    {
        return $this->updateField("avatarLink", $newValue);
    }
    /**
    * setAllowed
    */
    public function setAllowed(?bool $newValue): UpdateReply
    {
        return $this->updateField("allowed", $newValue);
    }
    /**
    * setRate
    */
    public function setRate(?int $newValue): UpdateReply
    {
        return $this->updateField("rate", $newValue);
    }
    // Getters
    public function getAvatarLink(): ?int
    {
        return $this->getField("avatarLink");
    }
    public function getAllowed(): ?bool
    {
        return $this->getField("allowed");
    }
    public function getRate(): ?int
    {
        return $this->getField("rate");
    }
    // Loaders
    public function loadByAvatarLink(int $avatarLink): SingleLoadReply
    {
        return $this->loadByField(
            "avatarLink",
            $avatarLink
        );
    }
    public function loadByAllowed(bool $allowed): SingleLoadReply
    {
        return $this->loadByField(
            "allowed",
            $allowed
        );
    }
    public function loadByRate(int $rate): SingleLoadReply
    {
        return $this->loadByField(
            "rate",
            $rate
        );
    }
    public function relatedAvatar(): AvatarSet
    {
        $ids = [$this->getAvatarLink()];
        $collection = new AvatarSet();
        $collection->loadFromIds($ids);
        return $collection;
    }
    public function relatedTransactions(): TransactionsSet
    {
        $ids = [$this->getId()];
        $collection = new TransactionsSet();
        $collection->loadFromResellerLinks($ids);
        return $collection;
    }
}
