<?php

namespace App\Models\Sets;

use YAPF\Framework\DbObjects\CollectionSet\CollectionSet as CollectionSet;
use App\Models\Reseller as Reseller;

// Do not edit this file, rerun gen.php to update!
class ResellerSet extends CollectionSet
{
    public function __construct()
    {
        parent::__construct("App\Models\Reseller");
    }
    /**
     * getObjectByID
     * returns a object that matchs the selected id
     * returns null if not found
     * Note: Does not support bad Ids please use findObjectByField
     */
    public function getObjectByID($id): ?Reseller
    {
        return parent::getObjectByID($id);
    }
    /**
     * getFirst
     * returns the first object in a collection
     */
    public function getFirst(): ?Reseller
    {
        return parent::getFirst();
    }
    /**
     * getObjectByField
     * returns the first object in a collection that matchs the field and value checks
     */
    public function getObjectByField(string $fieldname, $value): ?Reseller
    {
        return parent::getObjectByField($fieldname, $value);
    }
    /**
     * current
     * used by foreach to get the object should not be called directly
     */
    public function current(): Reseller
    {
        return parent::current();
    }
    /**
     * uniqueIds
     * returns unique values from the collection matching that field
     * @return array<int>
     */
    public function uniqueIds(): array
    {
        return parent::uniqueArray("id");
    }
    /**
     * uniqueAvatarLinks
     * returns unique values from the collection matching that field
     * @return array<int>
     */
    public function uniqueAvatarLinks(): array
    {
        return parent::uniqueArray("avatarLink");
    }
    /**
     * uniqueAlloweds
     * returns unique values from the collection matching that field
     * @return array<bool>
     */
    public function uniqueAlloweds(): array
    {
        return parent::uniqueArray("allowed");
    }
    /**
     * uniqueRates
     * returns unique values from the collection matching that field
     * @return array<int>
     */
    public function uniqueRates(): array
    {
        return parent::uniqueArray("rate");
    }
    // Loaders
    /**
     * loadById
     * @return mixed[] [status =>  bool, count => integer, message =>  string]
    */
    public function loadById(
        int $id, 
        int $limit = 0, 
        string $orderBy = "id", 
        string $orderDir = "DESC"
    ): array
    {
        return $this->loadByField(
            "id", 
            $id, 
            $limit, 
            $orderBy, 
            $orderDir
        );
    }
    /**
     * loadFromIds
     * @return array<mixed> [status =>  bool, count => integer, message =>  string]
    */
    public function loadFromIds(array $values): array
    {
        return $this->loadIndexs("id", $values);
    }
    /**
     * loadByAvatarLink
     * @return mixed[] [status =>  bool, count => integer, message =>  string]
    */
    public function loadByAvatarLink(
        int $avatarLink, 
        int $limit = 0, 
        string $orderBy = "id", 
        string $orderDir = "DESC"
    ): array
    {
        return $this->loadByField(
            "avatarLink", 
            $avatarLink, 
            $limit, 
            $orderBy, 
            $orderDir
        );
    }
    /**
     * loadFromAvatarLinks
     * @return array<mixed> [status =>  bool, count => integer, message =>  string]
    */
    public function loadFromAvatarLinks(array $values): array
    {
        return $this->loadIndexs("avatarLink", $values);
    }
    /**
     * loadByAllowed
     * @return mixed[] [status =>  bool, count => integer, message =>  string]
    */
    public function loadByAllowed(
        bool $allowed, 
        int $limit = 0, 
        string $orderBy = "id", 
        string $orderDir = "DESC"
    ): array
    {
        return $this->loadByField(
            "allowed", 
            $allowed, 
            $limit, 
            $orderBy, 
            $orderDir
        );
    }
    /**
     * loadFromAlloweds
     * @return array<mixed> [status =>  bool, count => integer, message =>  string]
    */
    public function loadFromAlloweds(array $values): array
    {
        return $this->loadIndexs("allowed", $values);
    }
    /**
     * loadByRate
     * @return mixed[] [status =>  bool, count => integer, message =>  string]
    */
    public function loadByRate(
        int $rate, 
        int $limit = 0, 
        string $orderBy = "id", 
        string $orderDir = "DESC"
    ): array
    {
        return $this->loadByField(
            "rate", 
            $rate, 
            $limit, 
            $orderBy, 
            $orderDir
        );
    }
    /**
     * loadFromRates
     * @return array<mixed> [status =>  bool, count => integer, message =>  string]
    */
    public function loadFromRates(array $values): array
    {
        return $this->loadIndexs("rate", $values);
    }
    // Related loaders
    public function relatedAvatar(): AvatarSet
    {
        $ids = $this->uniqueAvatarLinks();
        $collection = new AvatarSet();
        $collection->loadFromIds($ids);
        return $collection;
    }
    public function relatedTransactions(): TransactionsSet
    {
        $ids = $this->uniqueIds();
        $collection = new TransactionsSet();
        $collection->loadFromResellerLinks($ids);
        return $collection;
    }
}
