<?php

namespace App\Models\Sets;

use YAPF\Framework\DbObjects\CollectionSet\CollectionSet as CollectionSet;
use App\Models\Server as Server;

// Do not edit this file, rerun gen.php to update!
class ServerSet extends CollectionSet
{
    public function __construct()
    {
        parent::__construct("App\Models\Server");
    }
    /**
     * getObjectByID
     * returns a object that matchs the selected id
     * returns null if not found
     * Note: Does not support bad Ids please use findObjectByField
     */
    public function getObjectByID($id): ?Server
    {
        return parent::getObjectByID($id);
    }
    /**
     * getFirst
     * returns the first object in a collection
     */
    public function getFirst(): ?Server
    {
        return parent::getFirst();
    }
    /**
     * getObjectByField
     * returns the first object in a collection that matchs the field and value checks
     */
    public function getObjectByField(string $fieldname, $value): ?Server
    {
        return parent::getObjectByField($fieldname, $value);
    }
    /**
     * current
     * used by foreach to get the object should not be called directly
     */
    public function current(): Server
    {
        return parent::current();
    }
    /**
     * uniqueIds
     * returns unique values from the collection matching that field
     * @return array<int>
     */
    public function uniqueIds(): array
    {
        return parent::uniqueArray("id");
    }
    /**
     * uniqueDomains
     * returns unique values from the collection matching that field
     * @return array<string>
     */
    public function uniqueDomains(): array
    {
        return parent::uniqueArray("domain");
    }
    /**
     * uniqueControlPanelURLs
     * returns unique values from the collection matching that field
     * @return array<string>
     */
    public function uniqueControlPanelURLs(): array
    {
        return parent::uniqueArray("controlPanelURL");
    }
    // Loaders
    /**
     * loadById
     * @return mixed[] [status =>  bool, count => integer, message =>  string]
    */
    public function loadById(
        int $id, 
        int $limit = 0, 
        string $orderBy = "id", 
        string $orderDir = "DESC"
    ): array
    {
        return $this->loadByField(
            "id", 
            $id, 
            $limit, 
            $orderBy, 
            $orderDir
        );
    }
    /**
     * loadFromIds
     * @return array<mixed> [status =>  bool, count => integer, message =>  string]
    */
    public function loadFromIds(array $values): array
    {
        return $this->loadIndexs("id", $values);
    }
    /**
     * loadByDomain
     * @return mixed[] [status =>  bool, count => integer, message =>  string]
    */
    public function loadByDomain(
        string $domain, 
        int $limit = 0, 
        string $orderBy = "id", 
        string $orderDir = "DESC"
    ): array
    {
        return $this->loadByField(
            "domain", 
            $domain, 
            $limit, 
            $orderBy, 
            $orderDir
        );
    }
    /**
     * loadFromDomains
     * @return array<mixed> [status =>  bool, count => integer, message =>  string]
    */
    public function loadFromDomains(array $values): array
    {
        return $this->loadIndexs("domain", $values);
    }
    /**
     * loadByControlPanelURL
     * @return mixed[] [status =>  bool, count => integer, message =>  string]
    */
    public function loadByControlPanelURL(
        string $controlPanelURL, 
        int $limit = 0, 
        string $orderBy = "id", 
        string $orderDir = "DESC"
    ): array
    {
        return $this->loadByField(
            "controlPanelURL", 
            $controlPanelURL, 
            $limit, 
            $orderBy, 
            $orderDir
        );
    }
    /**
     * loadFromControlPanelURLs
     * @return array<mixed> [status =>  bool, count => integer, message =>  string]
    */
    public function loadFromControlPanelURLs(array $values): array
    {
        return $this->loadIndexs("controlPanelURL", $values);
    }
    // Related loaders
    public function relatedStream(): StreamSet
    {
        $ids = $this->uniqueIds();
        $collection = new StreamSet();
        $collection->loadFromServerLinks($ids);
        return $collection;
    }
}
