<?php

namespace App\Models;

use YAPF\Framework\DbObjects\GenClass\GenClass as GenClass;
use YAPF\Framework\Responses\DbObjects\UpdateReply as UpdateReply;
use YAPF\Framework\Responses\DbObjects\SingleLoadReply as SingleLoadReply;
use App\Models\Sets\PackageSet as PackageSet;

// Do not edit this file, rerun gen.php to update!
class Template extends genClass
{
    protected $use_table = "template";
    // Data Design
    protected $fields = [
        "id",
        "name",
        "detail",
        "notecardDetail",
    ];
    protected $dataset = [
        "id" => ["type" => "int", "value" => null],
        "name" => ["type" => "str", "value" => null],
        "detail" => ["type" => "str", "value" => null],
        "notecardDetail" => ["type" => "str", "value" => null],
    ];
    // Setters
    /**
    * setName
    */
    public function setName(?string $newValue): UpdateReply
    {
        return $this->updateField("name", $newValue);
    }
    /**
    * setDetail
    */
    public function setDetail(?string $newValue): UpdateReply
    {
        return $this->updateField("detail", $newValue);
    }
    /**
    * setNotecardDetail
    */
    public function setNotecardDetail(?string $newValue): UpdateReply
    {
        return $this->updateField("notecardDetail", $newValue);
    }
    // Getters
    public function getName(): ?string
    {
        return $this->getField("name");
    }
    public function getDetail(): ?string
    {
        return $this->getField("detail");
    }
    public function getNotecardDetail(): ?string
    {
        return $this->getField("notecardDetail");
    }
    // Loaders
    public function loadByName(string $name): SingleLoadReply
    {
        return $this->loadByField(
            "name",
            $name
        );
    }
    public function loadByDetail(string $detail): SingleLoadReply
    {
        return $this->loadByField(
            "detail",
            $detail
        );
    }
    public function loadByNotecardDetail(string $notecardDetail): SingleLoadReply
    {
        return $this->loadByField(
            "notecardDetail",
            $notecardDetail
        );
    }
    public function relatedPackage(?array $limitFields = null): PackageSet
    {
        $ids = [$this->getId()];
        $collection = new PackageSet();
        if ($limitFields !== null) {
            $collection->limitFields($limitFields);
        }
        $collection->loadFromTemplateLinks($ids);
        return $collection;
    }
}
