class Timezones extends genClass
{
    protected $use_table = "timezones";
    // Data Design
    protected $fields = [
        "id",
        "name",
        "code",
    ];
    protected $dataset = [
        "id" => ["type" => "int", "value" => null],
        "name" => ["type" => "str", "value" => null],
        "code" => ["type" => "str", "value" => null],
    ];
    // Setters
    /**
    * setName
    * @return mixed[] [status =>  bool, message =>  string]
    */
    public function setName(?string $newvalue): array
    {
        return $this->updateField("name", $newvalue);
    }
    /**
    * setCode
    * @return mixed[] [status =>  bool, message =>  string]
    */
    public function setCode(?string $newvalue): array
    {
        return $this->updateField("code", $newvalue);
    }
    // Getters
    public function getName(): ?string
    {
        return $this->getField("name");
    }
    public function getCode(): ?string
    {
        return $this->getField("code");
    }
    // Loaders
    public function loadByName(string $name): bool
    {
        return $this->loadByField(
            "name",
            $name
        );
    }
    public function loadByCode(string $code): bool
    {
        return $this->loadByField(
            "code",
            $code
        );
    }
    public function relatedSlconfig(): SlconfigSet
    {
        $ids = [$this->getId()];
        $collection = new SlconfigSet();
        $collection->loadFromDisplayTimezoneLinks($ids);
